import { HrefAttValue } from '../../../lib/attrs/hrefAttValue.js';

describe('test getID()', () => {
  const tests = [
    { in: '#a', exp: 'a' },
    { in: '#%E5%BA%B7', exp: '康' },
    { in: 'http://whatever.com', exp: undefined },
  ];

  for (const test of tests) {
    it(`${test.in}`, () => {
      const obj = new HrefAttValue(test.in);
      expect(obj.getReferencedID()).toBe(test.exp);
    });
  }
});

